import React from "react";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      homes: [],
    };
  }

  componentDidMount() {
    fetch("https://randomuser.me/api")
      .then((res) => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            homes: result,
          });
        },
        // error handler
        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
  }

  render() {
    const { error, isLoaded, homes } = this.state;

    if (error) {
      return <div className="col">Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div className="col">Loading...</div>;
    } else {
      return (
        <div className="col">
          <h1>Mi Casa</h1>
          <p>This is my house y&apos;all!</p>
          {homes.map((home) => (
            <div>{home.name}</div>
          ))}
        </div>
      );
    }
  }
}

export default App;
